import React from "react";
import "./App.css";
import profilePic from "./assets/profile.jpg"; // make sure you add this picture

function App() {
  return (
    <div className="app">
      <header className="header">
        <div className="profile-card">
          <img src={profilePic} alt="Mercy Cherop Koech" className="profile-pic" />
          <h1 className="name">Mercy Cherop Koech</h1>
          <h2 className="role">Web Developer</h2>
          <p className="intro">
            Hi, I’m <span className="highlight">Mercy</span> — a passionate web developer skilled in HTML, CSS,
            JavaScript, PHP, MySQL, and React. I love creating stylish, responsive, and user-friendly websites. ✨
          </p>
          <a href="cv.pdf" className="cv-button" download>
            Download CV
          </a>
        </div>
      </header>
    </div>
  );
}

export default App;
