import profilePic from "./assets/profile.jpg";

function App() {
  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-900 via-pink-700 to-pink-500 text-white flex flex-col items-center justify-center p-6">

      {/* Profile Picture */}
      <img
        src={profilePic}
        alt="Mercy Cherop Koech"
        className="w-48 h-48 rounded-2xl shadow-lg border-4 border-pink-300 mb-4"
      />

      {/* Name */}
      <h1 className="text-5xl font-extrabold text-pink-200 drop-shadow-lg animate-pulse">
        Mercy Cherop Koech
      </h1>

      {/* Role */}
      <p className="text-2xl mt-2 text-pink-100 italic tracking-wide">
        Hi, I'm <span className="font-semibold text-white">a Web Developer</span>
      </p>

      {/* Buttons */}
      <div className="mt-6 flex space-x-4">
        <a
          href="/Mercy_CV.pdf"
          download
          className="px-6 py-3 bg-pink-600 text-white font-semibold rounded-lg shadow-lg animate-bounce
          hover:bg-pink-500 hover:shadow-pink-400 hover:shadow-xl transition-all duration-300"
        >
          Download CV
        </a>

        <a
          href="#contact"
          className="px-6 py-3 bg-transparent border-2 border-white rounded-lg hover:bg-white hover:text-pink-700 transition-all duration-300"
        >
          Contact Me
        </a>
      </div>

      {/* Contact Section */}
      <div id="contact" className="mt-10 w-full max-w-lg bg-white text-pink-900 rounded-2xl p-6 shadow-lg">
        <h2 className="text-3xl font-bold mb-4">Contact Me</h2>
        <p>Email: <a href="mailto:merzkorchy@gmail.com" className="text-pink-600 font-semibold">merzkorchy@gmail.com</a></p>
        <p className="mt-2">Letâ€™s build something amazing together ðŸš€</p>
      </div>
    </div>
  );
}

export default App;
